using Nethereum.XUnitEthereumClients;
using System;
using System.Numerics;
using System.Threading;
using System.Threading.Tasks;

namespace NftSample
{
    class Program
    {
        static bool isRun_ = true;

        static void Main(string[] args)
        {
            ParserParams();
        }

        static void ParserParams()
        {
            Console.WriteLine("Init Start");
            string contractAddress = "0xa9f10692014b72e122b4daa2d3da461e366154af";
            var nft = new NftLib(contractAddress);     // ropsten
            Console.WriteLine("Init Complete");
            while (isRun_)
            {
                string line = Console.ReadLine();
                Task.Run(() => Params(nft, line)).Wait();
            }
        }

        static async void Params(NftLib nft, string line)
        {
            var text = line.ToLower();
            string[] split = null;
            if (text.Contains(":"))
            {
                split = text.Split(":");
                text = split[0];
            }

            switch (text)
            {
                case "eixt":
                case "quit":
                    isRun_ = false;
                    break;

                case "create":      // Contract 생성
                    {
                        string baseUri = "";// "https://www.dropbox.com/";
                        string name = "BreedGuppy";
                        string symbol = "BGP";
                        string bytecode = "";
                        var receipt = await nft.CreateNFTToken(baseUri, name, symbol, bytecode);
                        Console.WriteLine($"create ContractAddress:{receipt?.ContractAddress} 이주소를 복사해서 프로젝트에 적용한다.");
                    }
                    break;

                case "total":       // 생성된 Token 갯수 확인
                    {
                        var totalCnt = await nft.TotalTokenCount();
                        Console.WriteLine($"total:{totalCnt}");
                    }
                    break;

                case "mint":        // 토큰 추가
                    {
                        string to = "";
                        string guppyInfo = "guppyinfo:" + DateTime.Now.ToString();
                        if (null != split)
                        {
                            if (2 == split.Length) to = split[1];
                            else if (3 <= split.Length) guppyInfo = split[2];
                        }
                        var receipt = await nft.SafeMintGuppy(to, guppyInfo);
                        Console.WriteLine($"mint receipt Status:{receipt?.Status}");
                    }
                    break;

                case "ownerof":     // 소유자 확인
                    {
                        BigInteger tokenId = 0;
                        if (null != split && 2 <= split.Length) tokenId = BigInteger.Parse(split[1]);
                        var address = await nft.OwnerOfToken(tokenId);
                        Console.WriteLine($"ownerof tokenId:{tokenId} OwnerOf:{address}");
                    }
                    break;

                case "uri":         // 토큰의 uri 내용 확인
                    {
                        BigInteger tokenId = 0;
                        if (null != split && 2 <= split.Length) tokenId = BigInteger.Parse(split[1]);
                        var uri = await nft.TokenURI(tokenId);
                        Console.WriteLine($"uri tokenId:{tokenId} Uri:{uri}");
                    }
                    break;

                case "approve":     // 거래 권한 주소 추가
                    {
                        string address = "0xD08BC093f4Acc6Ebe21842351DBF74273c445A01";
                        BigInteger tokenId = 0;
                        if (null != split && 2 <= split.Length) tokenId = BigInteger.Parse(split[1]);
                        var result = await nft.Approve(address, tokenId);
                        Console.WriteLine($"approve address:{address}, tokenId:{tokenId}, result:{result}");
                    }
                    break;

                case "getapproved":         // 거래 권한자 확인
                    {
                        BigInteger tokenId = 0;
                        if (null != split && 2 <= split.Length) tokenId = BigInteger.Parse(split[1]);
                        var address = await nft.GetApproved(tokenId);
                        Console.WriteLine($"getApproved tokenId:{tokenId} address:{address}");
                    }
                    break;

                case "guppyinfo":           // guppyinfo 확인
                    {
                        BigInteger tokenId = 0;
                        if (null != split && 2 <= split.Length) tokenId = BigInteger.Parse(split[1]);
                        var guppyinfo = await nft.GuppyInfo(tokenId);
                        Console.WriteLine($"GuppyInfo tokenId:{tokenId} guppyInfo:{guppyinfo}");
                    }
                    break;

                default:
                    Console.WriteLine($"wrong text:{text}");
                    break;
            }
        }
    }
}
